sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	// Modified the if to return if the newValue == oldValue to avoid
    // unecessary trips to the server
    if (isLoading && !g_form.isNewRecord()) {
        return;
    }

	var getDate = new GlideAjax('MyDateTimeAjax'); 
	getDate.addParam('sysparm_name', 'nowDateTime'); 
	getDate.getXML(populateDateField); 

	// When the response is back from the server
    function populateDateField(response){
        var dateFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('u_when_needed');
        g_form.setValue('u_when_needed',dateFromScriptInclude);
    }


}
